import { RenderingUtils } from '../common/RenderingUtils';
const browser = require('webextension-polyfill');

const PROPEL_FILES_ALERT_CLASSNAME = 'js-toggle-propel-files-alert';
const FILTERS_LIST_SELECTOR = '#files_bucket .js-file-filter-form .p-2';


/**
 * In the github merge request hides automatically the propel files
 * ability to remove the filter
 */
const PullRequest = class PullRequest {

  constructor() {
    this.basePropelFilesDomList = [];
    this.otherPropelFilesDomList = [];
    this.toolbarElement = null;
    this.BASE_PROPEL_CLASS_REGEXP = new RegExp('.*/Model/.*(Base|Map).*.php');
    this.OTHER_PROPEL_CLASS_REGEXP = new RegExp('.*/Model/.*.php');
  }

  init() {
    // get the option set for 
    this.autoHideBasePropelFiles = true;
    chrome.storage.local.get(
      // eslint-disable-next-line camelcase
      {auto_hide_base_propel_files: '1'},
      (result) => {
        this.autoHideBasePropelFiles = result.auto_hide_base_propel_files === '1';
        this.addFilesPresent();
        this.listenForNewFiles();
      });
  }

  static get ALERT_TEMPLATE() {
    return `<div class="${PROPEL_FILES_ALERT_CLASSNAME} border rounded-1 ml-2  mr-1 p-2 px-2 py-1 rounded-1 f6"
      style="display: inline;position: relative;left: 10px;"></div>`;
  }

  static get FILTER_TEMPLATE() {
    return `<div class="d-flex">
      <label class="pl-1 mb-1" style="cursor:pointer;" role="menuitem" tabindex="0">
        <input
            class="js-diff-file-type-%filterType%"
            style="cursor:pointer;"
            type="checkbox"
            value="%filterType%"
            name="file-custom-filters[]"
        >%filterLabel% (<span class="text-normal js-file-type-count">0</span>)
      </label>
    </div>`;
  }

  static get TOOLBAR_CLASSNAME() {
    return '#files_bucket .pr-toolbar';
  }

  get toolbar() {
    if (this.toolbarElement === null) {
      this.toolbarElement = document.querySelector(PullRequest.TOOLBAR_CLASSNAME);
    }

    return this.toolbarElement;
  }

  get toolbarAlertElement() {
    const toolbar = this.toolbar;
    if (toolbar) {
      return toolbar.querySelector(`.${PROPEL_FILES_ALERT_CLASSNAME}`);
    }

    return null;
  }

  /**
   * Toggle the visibility of the filtered files (for the moment only the propel files)
   * @param {DOMElement} $button
   * @param {Array<DOMElement>} fileList
   */
  toggleFilteredFilesVisibility($button, fileList) {
    for (const fileElement of Object.values(fileList)) {
      this.toggleFileViewVisibility(fileElement, $button.checked);
    }
  }

  /**
   * mark file as viewed or unviewed
   * @param {DOMElement} fileElement
   * @param {Boolean} viewed
   */
  toggleFileViewedState(fileElement, viewed) {
    const reviewedFileForm = fileElement.querySelector('.js-toggle-user-reviewed-file-form');
    const reviewedFileCheckbox = reviewedFileForm.querySelector('.js-reviewed-checkbox');

    if (reviewedFileCheckbox.checked !== viewed) {
      reviewedFileCheckbox.click();
    }
  }

  /**
   * Hide or show given file diff view
   * @param {DOMElement} fileElement
   * @param {Boolean} visible
   */
  toggleFileViewVisibility(fileElement, visible) {
    const fileToggleButton = fileElement.querySelector('.js-details-target');
    const fileToggleExpanded = fileToggleButton.getAttribute('aria-expanded') === 'true';

    if (fileToggleExpanded !== visible) {
      fileToggleButton.click();
    }
  }

  /**
   * update the filters and count
   * displays an alert if at least one base propel is hidden
   * we suppose
   * - that files element is already present
   * - that if we call this at least one propel file is present
   */
  updateFilters() {
    // display the alert if not already displayed
    let toolbarAlertElement = this.toolbarAlertElement;
    if (toolbarAlertElement === null) {
      const alertParent = RenderingUtils.querySelectorHas(`${PullRequest.TOOLBAR_CLASSNAME } div.flex-1.flex-shrink-0`, '.diffbar-item.details-reset')?.[0]?.parentElement;
      if (alertParent) {
        alertParent.appendChild(RenderingUtils.createDomNodeFromHtmlTemplate(PullRequest.ALERT_TEMPLATE));
        toolbarAlertElement = this.toolbarAlertElement;
      }
    }

    // add filters
    let filtersList = document.querySelector(FILTERS_LIST_SELECTOR);
    this.updateFilter(filtersList, 'basePropelFiles', 'base propel files', this.basePropelFilesDomList, !this.autoHideBasePropelFiles);
    this.updateFilter(filtersList, 'otherPropelFiles', 'other propel files', this.otherPropelFilesDomList, true);

    // update alert
    if (this.basePropelFilesDomList.length > 0) {
      let title = `${this.basePropelFilesDomList.length} base propel could be auto viewed`;
      if (this.autoHideBasePropelFiles) {
        title = `${this.basePropelFilesDomList.length} base propel marked as viewed`;
      }
      if (toolbarAlertElement) {
        let label = `${this.basePropelFilesDomList.length} base propel detected`;
        let classRemove = 'bg-red-light';
        let classAdd = 'bg-green-light';
        if (this.autoHideBasePropelFiles) {
          label = `${this.basePropelFilesDomList.length} base propel`;
          classRemove = 'bg-green-light';
          classAdd = 'bg-red-light';
        }
        this.toolbarAlertElement.innerHTML = label;
        this.toolbarAlertElement.setAttribute('title', title);
        this.toolbarAlertElement.classList.remove(classRemove);
        this.toolbarAlertElement.classList.add(classAdd);
      } else {
        // eslint-disable-next-line no-alert
        alert(title);
      }
    }
  }

  /**
   * add/update the filter
   * @param {Array<DOMElement>} filtersList
   * @param {String} filterType
   * @param {String} filterLabel
   * @param {Array<DOMElement>} fileElementlist
   * @param {Boolean} defaultChecked true if checkbox should be initially checked
   */
  // eslint-disable-next-line max-params
  updateFilter(filtersList, filterType, filterLabel, fileElementlist, defaultChecked) {
    let filter = RenderingUtils.querySelectorHas('.d-flex', `.js-diff-file-type-${filterType}`, filtersList);
    let filterDiv = null;

    // add the filter if not existent
    if (filter.length === 0) {
      filter = RenderingUtils.createDomNodeFromHtmlTemplate(
        PullRequest.FILTER_TEMPLATE,
        {
          filterType,
          filterLabel
        }
      );
      filterDiv = document.createElement('div');
      filterDiv.appendChild(filter);
      document.querySelector(`${FILTERS_LIST_SELECTOR} .ml-1`).insertAdjacentElement(
        'beforeend',
        filterDiv
      );
      filterDiv.onchange = (event) => {
        if (event.target.type === 'checkbox') {
          this.toggleFilteredFilesVisibility(event.target, fileElementlist);
        }

        return false;
      };
    } else {
      filterDiv = filter[0];
    }

    // update the filter
    filterDiv.querySelector('input[type=checkbox]').checked = defaultChecked;
    filterDiv.querySelector('.js-file-type-count').innerText = fileElementlist.length;
  }

  /**
   * dispatch $elem upon basePropelFilesDomList or otherPropelFilesDomList
   * updateFilters if new file is added in one of the two lists
   * @param {DOMElement} $elem
   */
  checkFile($elem) {
    const fileName = $elem.querySelector('.file-info a').getAttribute('title');
    let newFileAdded = false;
    // eslint-disable-next-line no-console
    console.debug(`check file : ${fileName}`);
    if (this.BASE_PROPEL_CLASS_REGEXP.test(fileName)) {
      // eslint-disable-next-line no-console
      console.debug(`detected base propel file : ${fileName}`);
      if (this.autoHideBasePropelFiles) {
        this.toggleFileViewedState($elem, true);
      }
      this.basePropelFilesDomList.push($elem);
      newFileAdded = true;
    } else if (this.OTHER_PROPEL_CLASS_REGEXP.test(fileName)) {
      // eslint-disable-next-line no-console
      console.debug(`detected other propel file : ${fileName}`);
      this.otherPropelFilesDomList.push($elem);
      newFileAdded = true;
    } else {
      // eslint-disable-next-line no-console
      console.debug(`detected other file : ${fileName}`);
    }
    if (newFileAdded) {
      this.updateFilters();
    }
  }

  /**
   * add the files already loaded on the page before ajax query terminated
   */
  addFilesPresent() {
    document.querySelectorAll('#files .js-file-header').forEach((element) => {
      // eslint-disable-next-line no-invalid-this
      this.checkFile(element);
    });
  }

  /**
   * listen for new files added via ajax queries
   */
  listenForNewFiles() {
    document.getElementById('files').addEventListener(
      'DOMNodeInserted', (e) => {
        if (typeof e.target.dataset !== 'undefined' && e.target.dataset.fileType === '.php') {
          this.checkFile(e.target);
        }
      }
    );
  }

};

