/* global fetch, document, window, console, CustomEvent, JiraSettings */
// eslint-disable-next-line no-unused-vars
import { JiraIssueDataWrapper } from './JiraIssueDataWrapper';
import { JiraRenderer } from './JiraRenderer';
import { LoadingProgress } from './LoadingProgress';
import { Log } from 'CommonLib/Log';
import { LogsRenderer } from './LogsRenderer';
import { RenderingUtils } from 'CommonLib/RenderingUtils';
import { ToolbarsContainerRenderer } from './ToolbarsContainerRenderer';

export default class JiraController {

  constructor() {
    // this trick is used because jira seems to add nodes twice for the same issue
    this._locks = {};

    // the main issue key
    this.pageIssueKey = null;

    // lazy loaded components
    this._loadingProgress = null;
    this._renderer = null;
    this._toolbarsContainerRenderer = null;
  }

  get loadingProgress() {
    if (!this._loadingProgress) {
      this._loadingProgress = new LoadingProgress();
    }

    return this._loadingProgress;
  }

  /**
   * div container that will be used for all the toolbars
   * @return {ToolbarsContainerRenderer}
   */
  get toolbarsContainerRenderer() {
    if (!this._toolbarsContainerRenderer) {
      this._toolbarsContainerRenderer = new ToolbarsContainerRenderer();
    }

    return this._toolbarsContainerRenderer;
  }

  get renderer() {
    if (!this._renderer) {
      this._renderer = new JiraRenderer();
    }

    return this._renderer;
  }

  /**
   * This method is used to detect
   * - when the issue is changing or loaded via ajax (via DOMNodeInserted event listener)
   * - or the first time the issue page is loaded (no ajax call)
   */
  run() {
    Log.addLog('Jira support running', Log.LEVEL_DEBUG);

    const subNodeSelector = RenderingUtils.isRapidBoardView ? '#ghx-detail-issue' : '.aui-toolbar2-primary';
    const issueContainer = RenderingUtils.isRapidBoardView
      ? document.querySelector('.ghx-detail-view')
      : document.querySelector('.content');

    const domNodeInsertedMatcher = RenderingUtils.isRapidBoardView
      ? e => e.relatedNode.id === 'ghx-detail-contents' && e.relatedNode.querySelector(subNodeSelector)
      : e => e.relatedNode.classList.contains('command-bar') && e.relatedNode.querySelector(subNodeSelector);

    issueContainer.addEventListener(
      'DOMNodeInserted', (e) => {
        if (domNodeInsertedMatcher(e)) {
          this.loadInterface();
        }
      }
    );
    if (!RenderingUtils.isRapidBoardView) {
      // issue is rendered during page load (no ajax call)
      if (issueContainer.querySelector('#issue-content')) {
        this.loadInterface();
      }
    }
  }

  /**
   * main method that will do all necessary ajax calls
   * and create all toolbars
   * manage logs and loading progress
   */
  async loadInterface() {
    try {
      const pageIssueKey = this.lockPageIssueKeyFromUrl();

      this.loadingProgress.init();

      this.fetchIssueDataFromJiraAPI()
        // finish here in case of error
        .catch((error) => {
          Log.addLog(`unable to get data from jira for ${pageIssueKey}`, Log.LEVEL_CRITICAL, { error });
          throw error;
        })
        // render issue for each repo
        .then((issueData) => {
          this._issueData = new JiraIssueDataWrapper(issueData);
          if (this._issueData.isEpic) {
            return this.fetchEpicIssuesFromJira(this._issueData.key);
          }

          return null;
        })
        .then((epicIssues) => {
          if (this._issueData.isEpic) {
            this._issueData.fields.epicIssues = epicIssues?.issues;
          }

          return this.generateBranches(this._issueData, false, this.renderBranchCallback.bind(this));
        })
        .then(promises => Promise.all(promises))
        // catch generate branches errors
        .catch((error) => {
          if (!error?.logged) {
            Log.addIssueLog(
              { key: this.pageIssueKey }, `Error while fetching data for ${this.pageIssueKey}`,
              Log.LEVEL_ERROR, { error }
            );
          }
        })
        // render deep analysis button
        .then(() => new Promise((resolve) => {
          this.renderer.renderDeepAnalysisButton(this._issueData, this.deepAnalysisButtonCallback.bind(this));
          resolve();
        }))
        // catch rendering errors
        .catch((error) => {
          if (!error?.logged) {
            Log.addIssueLog(
              { key: this.pageIssueKey }, `Error while rendering issue ${this.pageIssueKey}`,
              Log.LEVEL_ERROR, { error }
            );
          }
        })
        .finally(() => {
          // remove the double loading lock
          delete this._locks[this.pageIssueKey];

          LogsRenderer.renderErrors(this.pageIssueKey);

          // we can remove progressBar if it's not done yet
          this.loadingProgress.remove();
        });
    } catch (error) {
      if (!error?.logged) {
        // eslint-disable-next-line no-console
        console.error('unexpected error', error);
      }
    }
  }

  /**
  * callback triggered when user click on deep analysis button
  */
  async deepAnalysisButtonCallback() {
    try {
      this.lockPageIssueKeyFromUrl();
      this.toolbarsContainerRenderer.remove();
      LogsRenderer.remove();
      this.loadingProgress.init();

      this.generateBranches(this._issueData, true, this.renderBranchCallback.bind(this))
        .then(promises => Promise.all(promises))
        .catch((error) => {
          if (!error?.logged) {
            Log.addIssueLog(
              { key: this.pageIssueKey }, `Error while fetching data for ${this.pageIssueKey}`,
              Log.LEVEL_ERROR, { error }
            );
          }
        })
        .finally(() => {
          // remove the double loading lock
          delete this._locks[this.pageIssueKey];
          LogsRenderer.renderErrors(this.pageIssueKey);
          // we can remove progressBar
          this.loadingProgress.remove();
        });
    } catch (error) {
      if (!error?.logged) {
        Log.addIssueLog(
          { key: this.pageIssueKey }, `Error while deep analysis of issue ${this.pageIssueKey}`,
          Log.LEVEL_ERROR, { error }
        );
      }
      // remove the double loading lock
      delete this._locks[this.pageIssueKey];
      LogsRenderer.renderErrors(this.pageIssueKey);
      // we can remove progressBar
      this.loadingProgress.remove();
    }
  }

  /**
   * Retrieve issue key from url
   * @throws {Error} log error and throws error if issue
   *   - not found
   *   - locked
   * @return {string} the issue key
   */
  lockPageIssueKeyFromUrl() {
    let matches = (/browse\/(?<projectKey1>[A-Z-a-z0-9]+-([0-9]+))|issues\/(?<projectKey2>[A-Z-a-z0-9]+-([0-9]+))\??|RapidBoard\.jspa\?.*selectedIssue=(?<projectKey3>[A-Z-a-z0-9]+-([0-9]+))/)
      .exec(window.location.href);

    this.pageIssueKey = null;
    if (matches && matches.length) {
      this.pageIssueKey = matches.groups.projectKey1 || matches.groups.projectKey2 || matches.groups.projectKey3;
      if (this._locks?.[this.pageIssueKey]) {
        Log.addLog('avoiding ck toolbar duplicate loading', Log.LEVEL_DEBUG);
        throw new Error('Issue Locked');
      }
      this._locks[this.pageIssueKey] = true;

      return this.pageIssueKey;
    }

    Log.addLog('No issue key', Log.LEVEL_CRITICAL);
    throw new Error('No issue key');
  }


  /**
    * @return {Promise<JiraIssue>}
    */
  async fetchIssueDataFromJiraAPI() {
    return await fetch(`https://jira.fchastanet.com/rest/api/2/issue/${this.pageIssueKey}`)
      .then((response) => {
        if (!response.ok) {
          throw Error(response.statusText);
        }

        return response.json();
      });
  }

  /**
   * grab data from jira for the epic's linked issues
   * @param {String} epicIssueKey
   * @return {JiraIssueDataWrapper}
   */
  async fetchEpicIssuesFromJira(epicIssueKey) {
    // eslint-disable-next-line arrow-body-style
    const jqlQuery = `"Epic Link" = ${epicIssueKey}`;

    return fetch('https://jira.fchastanet.com/rest/api/2/search', {
      method:  'POST',
      headers: {
        'Accept':       'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        jql:        jqlQuery,
        maxResults: 100,
        fields:     ['*all'],
        startAt:    0
      })
    })
      .then((response) => {
        if (!response.ok) {
          throw response;
        }

        return response.json();
      })
      .catch((error) => {
        Log.addIssueLog(
          this._issueData, 'Error while fetching data for epic issues',
          Log.LEVEL_ERROR, {error}
        );
        throw error;
      });
  }

  /**
   * grab data from jira for the linked issues
   * @param {Array} issuesToFetch
   */
  async fetchLinkedIssuesFromJira(issuesToFetch) {
    // eslint-disable-next-line arrow-body-style
    const jqlQuery = `key in (${issuesToFetch.map(d => `'${d}'`).join(',')})`;

    return fetch('https://jira.fchastanet.com/rest/api/2/search', {
      method:  'POST',
      headers: {
        'Accept':       'application/json',
        'Content-Type': 'application/json',
        'User-Agent':   'XX',
      },
      body: JSON.stringify({
        jql:        jqlQuery,
        maxResults: issuesToFetch.length,
        fields:     ['*all'],
        startAt:    0
      })
    })
      .then((response) => {
        if (!response.ok) {
          throw response;
        }

        return response.json();
      })
      .then(data => data)
      .catch((error) => {
        Log.addIssueLog(
          this._issueData, 'Error while fetching data for linked issues',
          Log.LEVEL_ERROR, { error }
        );
        throw error;
      });
  }

  /**
   * Generate data needed by loadingProgress component and update it
   * @param {Array<key, {issue, linkType}>} issueList list of issues to load
   * @return {Object<key, Boolean>} list of keys to be loaded
   *   special key fetchLinkedIssuesFromJira contains the linked issue key array
   */
  initLoadingProgressFromIssueList(issueList) {
    // calulate the keys for the loading progress bar
    const elementsToLoad = {};
    elementsToLoad.fetchLinkedIssuesFromJira = [];
    Object.values(issueList).forEach(({ issue, linkType }) => {
      issue.projectRepos.forEach((repo) => {
        const key = `${repo.github.repo}_${issue.key}`;
        elementsToLoad[`${key}_fetchBranch`] = true;
        elementsToLoad[`${key}_render`] = true;
      });
      elementsToLoad.fetchLinkedIssuesFromJira.push(issue.key);
    });
    this.loadingProgress.elementsToLoad = elementsToLoad;

    return elementsToLoad;
  }

  /**
   * for given issue triggers the rendering of
   * - this issue
   * - parent issue
   * - sub issues
   * - linked issues
   *
   * each issue is represented by a Branch object containing BranchOptions object
   * BranchOptions object indicates some options to the toolbar
   *
   * @param {JiraIssueDataWrapper} issueData
   * @param {Boolean} deepAnalysis false, only this issue task is rendered
   *  commitDelta is only calculated if not rapidBoard or deepAnalysis
   * @param {CallableFunction} branchRenderCallback
   * @return {Promise[]} promise for rendering each branch
   */
  async generateBranches(issueData, deepAnalysis, branchRenderCallback) {
    const pageIssueKey = this.pageIssueKey;
    const issueList = issueData.getIssueList(RenderingUtils.isRapidBoardView, deepAnalysis);
    const issueMap = {};

    // pre init
    const elementsToLoad = this.initLoadingProgressFromIssueList(issueList);
    if (elementsToLoad.fetchLinkedIssuesFromJira.length > 0) {
      // fetch the linked issue from Jira first
      const linkedIssues = await this.fetchLinkedIssuesFromJira(elementsToLoad.fetchLinkedIssuesFromJira);
      linkedIssues.issues.forEach((issue) => {
        issueList[issue.key] = {
          issue,
          linkType: issueList[issue.key].linkType,
        };
      });

      // copy issues from issueList to issue converting json to JiraIssueDataWrapper
      Object.keys(issueList).forEach((key) => {
        const issueObj = issueList[key];
        let parent = null;
        if (issueList?.[issueObj.issue.fields?.parent?.key]) {
          parent = issueList[issueObj.issue.fields.parent.key].issue;
        }
        issueObj.issue = new JiraIssueDataWrapper(issueObj.issue, parent ? new JiraIssueDataWrapper(parent) : null);
        issueMap[issueObj.issue.key] = issueObj;
      });

      // correct loading progress (all the branches can now be calculated correctly)
      this.initLoadingProgressFromIssueList(issueMap);
    }
    // fetch linked issues finished
    document.dispatchEvent(new CustomEvent('progress', { detail: { elementCompleted: 'fetchLinkedIssuesFromJira' } }));

    let branchList = issueData.getBranchList(issueMap, RenderingUtils.isRapidBoardView, deepAnalysis);

    // if deep analysis, order the branches by :
    // - linkType (this element, parent, subtask, linked)
    //    inside same linkType, issues are ordered by repo, like in not deepAnalysis
    // - then by issue status category(unknown, todo, dev, review, qa, done)
    if (deepAnalysis) {
      const issueCategoryOrder = Object.keys(JiraSettings.ISSUE_CATEGORY_LABEL);
      branchList.sort((a, b) => {

        /** @var {Branch} a */
        /** @var {Branch} b */
        if (a.issue.id === b.issue.id) {
          // keep order of repository
          return a.order - b.order;
        }
        // issue link type
        if (a.branchOptions.linkType === b.branchOptions.linkType) {
          const aStatusCategory = JiraSettings.ISSUE_STATUS_CATEGORY_MAPPING?.[a.issue.fields.status.id] || JiraSettings.ISSUE_STATUS_CATEGORY_UNKNOWN;
          const bStatusCategory = JiraSettings.ISSUE_STATUS_CATEGORY_MAPPING?.[b.issue.fields.status.id] || JiraSettings.ISSUE_STATUS_CATEGORY_UNKNOWN;

          return issueCategoryOrder.indexOf(aStatusCategory) - issueCategoryOrder.indexOf(bStatusCategory);
        }

        return JiraIssueDataWrapper.ISSUE_LINK_TYPE_ORDER.indexOf(a.branchOptions.linkType)
          - JiraIssueDataWrapper.ISSUE_LINK_TYPE_ORDER.indexOf(b.branchOptions.linkType)
        ;
      });
      branchList.forEach((branch, index) => {
        branch.order = index;
      });
    }

    // launch the promises
    const promises = branchList.map((branch) => {
      branchRenderCallback(branch, deepAnalysis, false);

      return branch.fetchData()
        .then(() => {
          // fetch data finished
          document.dispatchEvent(new CustomEvent('progress', { detail: { elementCompleted: `${branch.uniqueKey.key}_fetchBranch` } }));
        })
        .catch((error) => {
          // fetch data finished
          document.dispatchEvent(new CustomEvent('progress', { detail: { elementCompleted: `${branch.uniqueKey.key}_fetchBranch` } }));
          if (!error?.logged) {
            Log.addIssueLog(
              issueData,
              `error while fetching branch data for issue ${pageIssueKey}`,
              Log.LEVEL_ERROR,
              { error }
            );
          }
        })
        .then(() => {
          branchRenderCallback(branch, deepAnalysis, true);
          // rendering finished
          document.dispatchEvent(new CustomEvent('progress', { detail: { elementCompleted: `${branch.uniqueKey.key}_render` } }));

          return branch;
        });
    });

    return promises;
  }

  /**
   * each branch promise will use this callback to render itself
   * @param {Branch} branch
   * @param {Boolean} deepAnalysis
   * @param {Boolean} full
   */
  renderBranchCallback(branch, deepAnalysis, full) {
    this.toolbarsContainerRenderer.renderBranch(branch, deepAnalysis, full);
  }

}

