const browser = require('webextension-polyfill');

var a;
b

require('es6-promise').polyfill();
require('isomorphic-fetch');
require('./translation.js');

/**
 * @file Manage the display of branch links on Jira. Rendering classes are in file `./branchLinkRendering.js`
 */
const jiraController = new JiraController();
browser.runtime.sendMessage({ type: MESSAGE_INJECT_JIRA_SETTINGS })
  .then((response) => {
    if (response?.loadJiraInterface) {
      jiraController.run();
    }
  })
